with st.form(key='movie_input_form'):
    title = st.text_input('Title:')
    release_date = st.text_input('Release Date:')
    type_ = st.text_input('Type (MOVIE/SHOW):')
    age_certification = st.selectbox('Age Certification:', age)
    runtime = st.number_input('Runtime:', min_value=0)
    genres = st.selectbox('Genres:', genres)
    production_countries = st.selectbox('Production Countries:', country)
    submit_button = st.form_submit_button(label='Predict')

if submit_button:
    if not all([title, release_date, type_, age_certification, runtime, genres, production_countries]):
        st.warning('Please enter all features for the movie.')
    else:
        input_data = [{
            'type': type_,
            'age_certification': age_certification,
            'runtime': runtime,
            'genres': genres,
            'production_countries': production_countries
        }]
        prediction = predict_imdb_score(input_data)[0]
        st.markdown(f"""
        <div class="movie-input">
            <p>Title: {title}</p>
            <p>Release Date: {release_date}</p>
            <p>Predicted IMDb score: {prediction:.2f}</p>
        </div>
        """, unsafe_allow_html=True)

